    subroutine TRANSLATE_ERRORS_STDCALL (error, errorcodes)
    !DEC$ ATTRIBUTES DLLEXPORT, decorate, alias : "TRANSLATE_ERRORS_STDCALL" :: TRANSLATE_ERRORS_STDCALL
    !DEC$ ATTRIBUTES stdcall, reference :: TRANSLATE_ERRORS_STDCALL
    implicit none



    !define input variables
    Integer, intent(in):: error
    !define output variables
    Character*255, intent(out):: errorcodes



    call TRANSLATE_ERRORS (error, errorcodes)

    end subroutine

 
    subroutine TRANSLATE_ERRORS(error, errorcodes)
    !DEC$ ATTRIBUTES DLLEXPORT :: TRANSLATE_ERRORS

    use error_description_module

    implicit none



    Integer, intent(in):: error
    Character*255, intent(out):: errorcodes
    Integer i
    
    do i=1,size(errors_desciptions)
        if (errors_desciptions(i).id == error) then
            errorcodes = errors_desciptions(i).description
            return
        endif
    enddo

    errorcodes = 'Error code not found!'

    end subroutine

 
  
   
