    implicit none



    !define input variables
    Integer, intent(in):: error
    !define output variables
    Integer IS_ERROR



    IS_ERROR_STDCALL = IS_ERROR(error)

    end function

 
    integer function IS_ERROR(error)
    !DEC$ ATTRIBUTES DLLEXPORT :: IS_ERROR

    use error_description_module

    implicit none

    Integer, intent(in):: error

    Integer i
    
    do i=1,size(errors_desciptions)
        if (errors_desciptions(i).id == error) then
            IS_ERROR = 1                    ! return 1, alternative: return error-number
            return
        endif
    enddo

    IS_ERROR = 0

    end function
